import React from "react";
import TextField  from "@mui/material/TextField";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import { Button, CardActionArea, CardActions } from '@mui/material';
import AppBar from '@mui/material/AppBar';
// import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import { Handle, Position } from 'react-flow-renderer';

export default function UserTextComponent(key){
    return (
        <>
        <Handle type="target" position={Position.Top} />
        <Card sx={{ maxWidth: '90vw'}} key = {key}>
            <CardActionArea>
                <CardContent sx = {{padding:'8px 15px'}}>
                    <AppBar position="static" color="secondary" sx = {{marginBottom:'6px',borderRadius:'10px'}} >
                        <Toolbar variant="dense" sx={{minHeight:'5%',justifyContent:'center'}}>
                        <Typography variant ="subtitle1" color="black" component="div"  sx={{lineHeight:'15px',fontSize :"0.75rem"}}>
                            User Response
                        </Typography>
                        </Toolbar>
                    </AppBar>
                    <TextField
                        id="outlined-multiline-flexible"
                        label="Enter User Response"
                        multiline
                        fullWidth
                        maxRows={4}
                        size = "Small"
                        //value={value}
                        //onChange={handleChange}
                    />
                </CardContent>
            </CardActionArea>
            <CardActions sx = {{paddingTop:'0px',height: "1vh"}}>
                <Button size="small" color="primary" sx={{fontSize:8,height:8}}>
                    Save
                </Button>
            </CardActions>
        </Card>
        <Handle type="source" position={Position.Bottom} id="2" />
        </>)
  };
