import logo from './logo.svg';
import './App.css';
import DrawerComponent from './js/Components/Drawer';
import { useState } from 'react';
import { breadcrumbsClasses } from '@mui/material';
import BotComponent from './js/Components/BotComponent';
import DrawingComponent from './js/Components/DrawingContainer';
import React, {useCallback} from 'react';
import YesNoComponent from './js/Components/YesNoComponent';
import ButtonResponseComponent from './js/Components/ButtonResponseComponent';
import UserTextComponent from './js/Components/UserTextComponent';
import StartComponent from './js/Components/StartComponent';
import { addEdge,useNodesState, useEdgesState } from 'react-flow-renderer';
import EndComponent from './js/Components/EndComponent';

const handleSaveToPC = () => {
  const jsonData = {name:'ABC', value: {name:'xyz', value: 123}}
  const fileData = JSON.stringify(jsonData);
  const blob = new Blob([fileData], {type: "text/plain"});
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.download = 'treeStructure.json';
  link.href = url;
  link.click();
}

const UserTextResponse = (key) => {
  return (<span style = {{backgroundColor:'green',width:'20vh',height:'10vh',margin:'1vh',padding:'20px',left:'50vh'}} key = {key}>User Text Component</span>);
};

const nodeTypes = { yesNoNode: YesNoComponent,
  buttonNode:ButtonResponseComponent,
  botNode: BotComponent,
  userText: UserTextComponent, 
  startNode: StartComponent,
  endNode: EndComponent};

function App() {
  const validateConnection = (connection) => {
    console.log('validation callback at app.js');
    if ((connection.source.indexOf('Start')>-1 && connection.target.indexOf('User') > -1) || (connection.source.indexOf('User')>-1 && connection.target.indexOf('User') > -1))
      return false
    return true
  }
  const initialNodes = [
    { 
        id: "Start",
        type: "startNode", // input node
        name:'startNode',
        data: { label: "Start Node" ,
        validationCallback:validateConnection},
        position: { x: 100, y: 0 },
    }
  ]
  const [children, setChildren] = useState([])
  // const [nodes, setNodes ] = useState(initialNodes);
  // const [edges, setEdges] = useState([]);
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);




  const onConnect = useCallback((/*{ source, target,sourceHandle,targetHandle }*/params) => {
    setEdges((edges) => {
      console.log({...params});
      return [
        ...edges,
        {
          ...params,
          id: Math.random().toString(),
          // source,
          // target,
          // sourceHandle,
          // targetHandle
        }
      ];
    });
  }, []);

  // const onConnect = useCallback((/*{ source, target,sourceHandle,targetHandle }*/params) => 
  //   setEdges((edges) => addEdge({ ...params, animated: true, style: { stroke: '#fff' } }, edges))
  // , []);
  const saveFlow = (event, responseType) =>{
      console.log('save flow!')
  }

  const clickEvent = (event,responseType) =>{
    if(responseType == 'save')
    {
      saveFlow();
      return;
    }
      setNodes((currentNodes) => {
        console.log(currentNodes);
        const node_id = Math.random()
        switch(responseType){
          case 'Bot': {
            return [
              ...currentNodes,
              {
                id: "Bot-"+node_id,
                position: { x: 100, y: 100 },
                type : 'botNode',
                // data:{label: (
                //       <>
                //       {/* <BotComponent /> */}
                //       <YesNoComponent />
                //       </>
                //   )},
                style: {
                    background: '#D6D5E6',
                    width: 200,
                },
                data: {
                  validationCallback: validateConnection,
                  node_id
                }
              }
            ];
          }
          break;
          case 'YesNo': {
            return [
              ...currentNodes,
              {
                id: "User-"+node_id,
                position: { x: 100, y: 100 },
                type : 'yesNoNode',
                style: {
                    background: '#D6D5E6',
                    width: 200,
                },
                data: {
                  validationCallback: validateConnection,
                  node_id
                }
              }
            ];
          }
          break;
          case 'button': {
            return [
              ...currentNodes,
              {
                id: "User-"+node_id,
                position: { x: 100, y: 100 },
                type : 'buttonNode',
                style: {
                    background: '#D6D5E6',
                    width: 200,
                },
                data: {
                  validationCallback: validateConnection,
                  node_id
                }
              }
            ];
          }
          break;
          case 'UserText': {
            return [
              ...currentNodes,
              {
                id: "User-"+node_id,
                position: { x: 100, y: 100 },
                type : 'userText',
                style: {
                    background: '#D6D5E6',
                    width: 200,
                },
                name:'userText',
                data: {
                  validationCallback: validateConnection,
                  node_id
                }
              }
            ];
          }
          break;
          case 'end': {
            return [
              ...currentNodes,
              {
                id: "end_"+node_id,
                position: { x: 100, y: 100 },
                type : 'endNode',
                name:'end',
                data: {
                  validationCallback: validateConnection,
                  node_id
                }
              }
            ];
          }
          break;
        }
      });
    }


  return (
    <div className="App">
      {/* <DrawingComponent /> */}
      <div className='app-body' style={{display:'flex'}}>
        <DrawerComponent clickEvent = {clickEvent} />
        <div className = 'drawing-space' style = {{height:'100vh',width:'100vw'}}>
          <DrawingComponent 
            nodes = {nodes} 
            setNodes = {setNodes} 
            edges = {edges} 
            setEdges = {setEdges} 
            onConnect = {onConnect} 
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            nodeTypes = {nodeTypes}
          />
        </div>
        <br/>
        {/* <button className = 'SaveButton' onClick = {handleSaveToPC}>Save JSON</button> */}
      </div>
    </div>
  );
}

export default App;
