import React, {useState} from "react";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import { CardActionArea, CardActions } from '@mui/material';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { Handle, Position } from 'react-flow-renderer';
const handleStyle = { left: 10 };



export default function YesNoComponent(key){
    const [buttons, setbuttons] = useState([])
    const [buttonCount, setButtonCount] = useState(0)

    const handleCountChange = (event,number) =>{
        console.log('Value changed!!',buttonCount)
        setButtonCount(event.target.value);
    }

    return (
        <>
            <Handle type="target" position={Position.Top} />
            <Card sx={{ maxWidth: '90vw'}} key = {key}>
                <CardActionArea>
                    <CardContent sx = {{padding:'8px 15px'}}>
                        <AppBar position="static" color="secondary" sx = {{marginBottom:'6px',borderRadius:'10px'}}>
                            <Toolbar variant="dense" sx={{minHeight:'5%',justifyContent:'center'}}>
                            <Typography variant ="subtitle1" color="black" component="div"  sx={{lineHeight:'15px',fontSize :"0.75rem"}}>
                                User Response
                            </Typography>
                            </Toolbar>
                        </AppBar>
                        {/* <Select
                            labelId="demo-simple-select-label"
                            id="demo-simple-select"
                            value={buttonCount}
                            label="Button Count"
                            // data-cy="select"
                            onChange={(event) => {
                               console.log('Value Changed')
                            }}
                        >
                            <option value={1}>1</option>
                            <option value={2}>2</option>
                            <option value={3}>3</option>
                        </Select> */}
                        <label>
                            Button Count&nbsp; 
                            <select
                                id="demo-simple-select"
                                value={buttonCount}
                                label="Button Count"
                                // data-cy="select"
                                onChange={handleCountChange}
                            >
                                <option value={0} >0</option>
                                <option value={1} >1</option>
                                <option value={2}>2</option>
                                <option value= {3}>3</option>
                                <option value={4}>4</option>
                            </select>
                        </label>
                        {
                            [...Array(buttonCount)].map((value)=>{
                                <p className="dynamicP" id={value} >Hey</p>
                            })


                        }
                        {/* <FormControl  sx={{ m: 2, minWidth: 120 }} size="small">
                            <InputLabel id="demo-simple-select-label">Button Count</InputLabel>
                            <Select
                            labelId="demo-simple-select-label"
                            id="demo-simple-select"
                            value={buttonCount}
                            label="Button Count"
                            onChange={handleCountChange}
                            >
                                <MenuItem value={1}>1</MenuItem>
                                <MenuItem value={2}>2</MenuItem>
                                <MenuItem value={3}>3</MenuItem>
                            </Select>
                        </FormControl> */}
                    </CardContent>
                </CardActionArea>
            </Card>
            <Handle type="source" position={Position.Bottom} id="a" style={{left: 50}} />
            <Handle type="source" position={Position.Bottom} id="b" style={{left: 150}} />
        </>
        )
  };
