import React from "react";
import Draggable from "react-draggable";
import TextField  from "@mui/material/TextField";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import { Button, CardActionArea, CardActions } from '@mui/material';
import AppBar from '@mui/material/AppBar';
// import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';

export default function BotComponent(key){
    return (
    <Draggable bounds="parent">
        <Card sx={{ maxWidth: '28vh' }} key = {key}>
            <CardActionArea>
                <CardContent sx = {{padding:'8px 15px'}}>
                    <AppBar position="static" sx = {{marginBottom:'6px',borderRadius:'10px'}}>
                        <Toolbar variant="dense" sx={{minHeight:'25%',justifyContent:'center'}}>
                        <Typography color="black" component="div" sx={{lineHeight:'30px'}}>
                            Bot Response
                        </Typography>
                        </Toolbar>
                    </AppBar>
                    <TextField
                        id="outlined-multiline-flexible"
                        label="Enter Response"
                        multiline
                        fullWidth
                        maxRows={4}
                        //value={value}
                        //onChange={handleChange}
                    />
                </CardContent>
            </CardActionArea>
            <CardActions sx = {{paddingTop:'0px'}}>
                <Button size="small" color="primary">
                    Save
                </Button>
            </CardActions>
        </Card>
    </Draggable>);
  };
