import React, {useState} from "react";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import { CardActionArea, CardActions } from '@mui/material';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { Handle, Position } from 'react-flow-renderer';
const handleStyle = { left: 10 };



export default function YesNoComponent(key){
    const [buttons, setbuttons] = useState([])
    const [buttonCount, setButtonCount] = useState(0)

    const handleCountChange = event =>{
        console.log('Value changed!!',buttonCount)
    }

    return (
        <>
            <Handle type="target" position={Position.Top} />
            <Card sx={{ maxWidth: '90vw'}} key = {key}>
                <CardActionArea>
                    <CardContent sx = {{padding:'8px 15px'}}>
                        <AppBar position="static" color="secondary" sx = {{marginBottom:'6px',borderRadius:'10px'}}>
                            <Toolbar variant="dense" sx={{minHeight:'5%',justifyContent:'center'}}>
                            <Typography variant ="subtitle1" color="black" component="div"  sx={{lineHeight:'15px',fontSize :"0.75rem"}}>
                                User Response
                            </Typography>
                            <FormControl fullWidth>
                                <InputLabel id="demo-simple-select-label">Button Count</InputLabel>
                                <Select
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                value={buttonCount}
                                label="Age"
                                onChange={handleCountChange}
                                >
                                    <MenuItem value={10}>Ten</MenuItem>
                                    <MenuItem value={20}>Twenty</MenuItem>
                                    <MenuItem value={30}>Thirty</MenuItem>
                                </Select>
                            </FormControl>
                            </Toolbar>
                        </AppBar>
                    </CardContent>
                </CardActionArea>
                <Stack direction="row"
                justifyContent="space-evenly"
                alignItems="center"
                spacing={2}
                sx = {{paddingBottom:1}}>
                    <Chip label="Yes" size="small" sx = {{fontSize:10}}/>
                    <Chip label="No" size="small" variant="outlined" sx = {{fontSize:10}}/>
                </Stack>
            </Card>
            <Handle type="source" position={Position.Bottom} id="a" style={{left: 50}} />
            <Handle type="source" position={Position.Bottom} id="b" style={{left: 150}} />
        </>
        )
  };
