import { breadcrumbsClasses } from '@mui/material';
import BotComponent from './js/Components/BotComponent';
import DrawingComponent from './js/Components/DrawingContainer';
import React from 'react';

const handleSaveToPC = () => {
  const jsonData = {name:'ABC', value: {name:'xyz', value: 123}}
  const fileData = JSON.stringify(jsonData);
  const blob = new Blob([fileData], {type: "text/plain"});
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.download = 'treeStructure.json';
  link.href = url;
  link.click();
}

const UserTextResponse = (key) => {
  return (<span style = {{backgroundColor:'green',width:'20vh',height:'10vh',margin:'1vh',padding:'20px',left:'50vh'}} key = {key}>User Text Component</span>);
};

const initialNodes = [
      {
          id: "1",
          type: "input", // input node
          data: { label: "Start Node" },
          position: { x: 100, y: 0 }
      }
]

function App() {
  const [children, setChildren] = useState([])
  const [nodes, setNodes] = useState(initialNodes);
  // const [userTextResponseChildren,setUserTextResponseChildren] = useState([])

  const addEdge = useCallback(({ source, target }) => {
    setEdges((edges) => {
      console.log(source, target);
      return [
        ...edges,
        {
          id: Math.random().toString(),
          source,
          target
        }
      ];
    });
  }, []);

  const clickEvent = (event,responseType) =>{
    console.log('Click event at App!!',responseType,event)
      setNodes((currentNodes) => {
        console.log(currentNodes);
        switch(responseType){
          case 'Bot': {
            return [
              ...currentNodes,
              {
                id: Math.random().toString(),
                position: { x: 100, y: 200 },
                data:{label: (
                      <>
                      <BotComponent />
                      </>
                  )},
                  style: {
                      background: '#D6D5E6',
                      width: 200,
                  }
              }
            ];
          }
          break;
        }
      });
    }


  return (
    <div className="App">
      {/* <DrawingComponent /> */}
      <div className='app-body' style={{display:'flex'}}>
        <DrawerComponent clickEvent = {clickEvent} />
        <div className = 'drawing-space' style = {{height:'90vh',width:'100vw'}}>
          <DrawingComponent nodes = {nodes} setNodes = {setNodes}/>
        </div>
        <br/>
        {/* <button className = 'SaveButton' onClick = {handleSaveToPC}>Save JSON</button> */}
      </div>
    </div>
  );
}

export default App;
